package mypackage;

public class Test01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Arithmetic Operators Demo");
		//variables
		int a=5,b=2;
		System.out.println("a+b:"+(a+b));
		System.out.println("a-b:"+(a-b));
		System.out.println("a*b:"+(a*b));
		//quotient
		System.out.println("a/b:"+((float)a/b));
		//remainder
		System.out.println("a%b:"+(a%b));
		System.out.println();
	}

}
