package mypackage;

public class Test07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Logical Operators:");
		int a=1,b=1;
		if((a==1)&&(b==1)) {
			System.out.println("yes");
		}
		if((a==1)&&(b==0)) {
			System.out.println("yes");
		}
		if((a==0)&&(b==1)) {
			System.out.println("yes");
		}
		if((a==0)&&(b==0)) {
			System.out.println("yes");
		}
	}

}
